import React from 'react';
import SideDrawer from "./components/SideDrawer/sidedrawer"
import Content from './components/MainContent/content'


class App extends React.Component{

   state = {
      sideDrawerVisible : false
   };

   drawerButtonToggle = () => {
      this.setState((prevState) => {
         return {sideDrawerVisible : !prevState.sideDrawerVisible};
      });
   };

   drawerButtonHide = () => {
      this.setState(() => {
         return {sideDrawerVisible : false};
      });
   };


   render(){

      let sidedrawer;
      
      if (this.state.sideDrawerVisible)
         sidedrawer  = <SideDrawer isOpen = {this.state.sideDrawerVisible}/>


      return(
         <div className="App">
            <SideDrawer isOpen = {this.state.sideDrawerVisible}/>
            <main style={{maxWidth:'100%'}}>
               <Content drawerClickHandler={this.drawerButtonToggle} drawerButtonHide ={this.drawerButtonHide}/>
            </main>
         </div>
      )
   }
}

export default App;
